#!/usr/bin/env python3
"""
Simple test to verify Llama4 tool calling works with Together API
"""

import os
import sys
import json
from typing import Dict, Any

def test_together_api():
    """Test basic Together API connectivity"""
    api_key = os.environ.get("TOGETHER_API_KEY")
    if not api_key:
        print(" TOGETHER_API_KEY not found")
        return False

    print(f" API key found (ending in: ...{api_key[-4:]})")
    return True

def make_direct_api_call():
    """Make a direct API call to Together without using llama-stack"""
    import requests

    api_key = os.environ.get("TOGETHER_API_KEY")

    # Simple tool calling test
    tools = [
        {
            "type": "function",
            "function": {
                "name": "get_weather",
                "description": "Get current weather in a location",
                "parameters": {
                    "type": "object",
                    "properties": {
                        "location": {
                            "type": "string",
                            "description": "The city and state, e.g. San Francisco, CA"
                        }
                    },
                    "required": ["location"]
                }
            }
        }
    ]

    payload = {
        "model": "meta-llama/Meta-Llama-3.1-8B-Instruct-Turbo",
        "messages": [
            {"role": "user", "content": "What's the weather like in San Francisco?"}
        ],
        "tools": tools,
        "tool_choice": "auto",
        "max_tokens": 512
    }

    headers = {
        "Authorization": f"Bearer {api_key}",
        "Content-Type": "application/json"
    }

    try:
        response = requests.post(
            "https://api.together.xyz/v1/chat/completions",
            json=payload,
            headers=headers,
            timeout=30
        )

        if response.status_code == 200:
            result = response.json()
            print(" Direct API call successful")

            # Check if tool call was made
            if "choices" in result and len(result["choices"]) > 0:
                message = result["choices"][0]["message"]
                if "tool_calls" in message and message["tool_calls"]:
                    print(" Tool call detected in response")
                    tool_call = message["tool_calls"][0]
                    print(f"   Function: {tool_call['function']['name']}")
                    print(f"   Arguments: {tool_call['function']['arguments']}")
                    return True
                else:
                    print("  No tool calls in response")
                    print(f"   Response: {message.get('content', 'No content')}")

            return True
        else:
            print(f" API call failed: {response.status_code}")
            print(f"   Response: {response.text}")
            return False

    except Exception as e:
        print(f" API call error: {e}")
        return False

if __name__ == "__main__":
    print(" Simple Together API Tool Calling Test")
    print("=" * 50)

    # Test 1: Check API key
    print("\n1. Checking API key...")
    if not test_together_api():
        sys.exit(1)

    # Test 2: Direct API call
    print("\n2. Testing direct API call...")
    try:
        if make_direct_api_call():
            print("\n All tests passed!")
        else:
            print("\n Direct API test failed")
            sys.exit(1)
    except ImportError as e:
        print(f" Missing dependency: {e}")
        print("   Try: pip install requests")
        sys.exit(1)
    except Exception as e:
        print(f" Unexpected error: {e}")
        sys.exit(1)

