from typing import Dict, List, Optional
from enum import Enum

class ModelProvider(str, Enum):
    OPENAI = "openai"
    ANTHROPIC = "anthropic"
    META = "meta"

class ModelConfig(BaseModel):
    """Pydantic model with advanced validation"""

    # Field with validation
    model_id: str = Field(..., min_length=1, description="Model identifier")
    provider: ModelProvider = Field(..., description="Model provider")

    # Field with custom validator
    temperature: float = Field(default=0.7, ge=0.0, le=2.0)
    max_tokens: Optional[int] = Field(default=None, gt=0, le=100000)

    # Complex field validation
    stop_sequences: List[str] = Field(default_factory=list, max_items=10)

    # Configuration
    class Config:
        use_enum_values = True  # Serialize enums as values
        validate_assignment = True  # Validate on assignment
        extra = "forbid"  # Reject extra fields

        # Custom JSON encoders
        json_encoders = {
            ModelProvider: lambda v: v.value
        }

    @validator('model_id')
    def validate_model_id(cls, v: str, values: Dict[str, Any]) -> str:
        """Custom model ID validation"""
        provider = values.get('provider')
        if provider == ModelProvider.OPENAI and not v.startswith('gpt'):
            raise ValueError('OpenAI models must start with "gpt"')
        elif provider == ModelProvider.ANTHROPIC and not v.startswith('claude'):
            raise ValueError('Anthropic models must start with "claude"')
        return v

    @root_validator
    def validate_provider_model_combination(cls, values: Dict[str, Any]) -> Dict[str, Any]:
        """Validate provider and model combination"""
        provider = values.get('provider')
        model_id = values.get('model_id')

        if provider and model_id:
            valid_combinations = {
                ModelProvider.OPENAI: ['gpt-3.5-turbo', 'gpt-4'],
                ModelProvider.ANTHROPIC: ['claude-3-sonnet', 'claude-3-opus'],
                ModelProvider.META: ['llama-2-7b', 'llama-2-13b']
            }

            if model_id not in valid_combinations.get(provider, []):
                available = valid_combinations.get(provider, [])
                raise ValueError(f"Invalid model {model_id} for provider {provider}. Available: {available}")

        return values

    def to_provider_config(self) -> Dict[str, Any]:
        """Convert to provider-specific configuration"""
        base_config = self.dict(exclude_none=True)

        # Provider-specific transformations
        if self.provider == ModelProvider.OPENAI:
            # OpenAI uses 'model' instead of 'model_id'
            base_config['model'] = base_config.pop('model_id')

        return base_config
