```python
from contextlib import asynccontextmanager, contextmanager
from typing import AsyncGenerator, Generator
import asyncio

class ResourcePool:
    """Thread-safe resource pool with context management"""

    def __init__(self, create_resource: Callable, max_size: int = 10):
        self.create_resource = create_resource
        self.max_size = max_size
        self.pool: asyncio.Queue = asyncio.Queue(maxsize=max_size)
        self.created_count = 0
        self.lock = asyncio.Lock()

    @asynccontextmanager
    async def acquire(self) -> AsyncGenerator[Any, None]:
        """Acquire resource from pool with automatic cleanup"""
        resource = None
        try:
            # Try to get existing resource
            try:
                resource = self.pool.get_nowait()
            except asyncio.QueueEmpty:
                # Create new resource if under limit
                async with self.lock:
                    if self.created_count < self.max_size:
                        resource = await self.create_resource()
                        self.created_count += 1
                    else:
                        # Wait for available resource
                        resource = await self.pool.get()

            yield resource

        except Exception:
            # Resource is corrupted, don't return to pool
            if resource:
                async with self.lock:
                    self.created_count -= 1
            raise
        else:
            # Return healthy resource to pool
            if resource:
                await self.pool.put(resource)

@asynccontextmanager
async def managed_inference_session(
    provider_config: dict,
    timeout: float = 30.0
) -> AsyncGenerator[InferenceProvider, None]:
    """Managed inference session with timeout and cleanup"""

    provider = None
    timeout_task = None

    try:
        # Initialize provider
        provider = await create_provider(provider_config)
        await provider.initialize()

        # Set up timeout
        timeout_task = asyncio.create_task(asyncio.sleep(timeout))

        yield provider

    except asyncio.TimeoutError:
        logger.warning(f"Inference session timed out after {timeout}s")
        raise
    finally:
        # Cleanup
        if timeout_task and not timeout_task.done():
            timeout_task.cancel()

        if provider:
            try:
                await provider.cleanup()
            except Exception as exc:
                logger.error(f"Error cleaning up provider: {exc}")

# Usage
async def example_usage():
    """Example of using managed resources"""

    # Resource pool usage
    pool = ResourcePool(create_resource=create_http_client, max_size=5)

    async with pool.acquire() as client:
        response = await client.get("https://api.example.com")

    # Managed session usage
    config = {"provider": "openai", "api_key": "..."}
    async with managed_inference_session(config, timeout=60.0) as provider:
        result = await provider.chat_completion(
            model_id="gpt-3.5-turbo",
            messages=[{"role": "user", "content": "Hello"}]
        )
