#### **Robust Configuration and Validation**
```python
from typing import Any, Dict, Optional, Union
from pathlib import Path
import os
import yaml
import json

class ConfigurationError(Exception):
    """Configuration-related errors"""
    pass

class RobustConfig:
    """Robust configuration management with validation and defaults"""

    def __init__(self, 
                 config_data: Optional[Dict[str, Any]] = None,
                 config_file: Optional[Union[str, Path]] = None,
                 env_prefix: str = "LLAMA_STACK_"):
        self.env_prefix = env_prefix
        self._data: Dict[str, Any] = {}
        self._defaults: Dict[str, Any] = {}
        self._validators: Dict[str, Callable] = {}

        # Load configuration from various sources
        if config_file:
            self._load_from_file(config_file)
        if config_data:
            self._data.update(config_data)
        self._load_from_env()

    def _load_from_file(self, config_file: Union[str, Path]):
        """Load configuration from file"""
        path = Path(config_file)
        if not path.exists():
            raise ConfigurationError(f"Config file not found: {path}")

        try:
            with open(path) as f:
                if path.suffix.lower() in ['.yaml', '.yml']:
                    data = yaml.safe_load(f)
                elif path.suffix.lower() == '.json':
                    data = json.load(f)
                else:
                    raise ConfigurationError(f"Unsupported config format: {path.suffix}")

            self._data.update(data or {})
        except Exception as exc:
            raise ConfigurationError(f"Error loading config from {path}: {exc}")

    def _load_from_env(self):
        """Load configuration from environment variables"""
        for key, value in os.environ.items():
            if key.startswith(self.env_prefix):
                config_key = key[len(self.env_prefix):].lower()

                # Try to parse as JSON first, fall back to string
                try:
                    parsed_value = json.loads(value)
                except json.JSONDecodeError:
                    parsed_value = value

                self._data[config_key] = parsed_value

    def set_default(self, key: str, value: Any):
        """Set default value for configuration key"""
        self._defaults[key] = value

    def set_validator(self, key: str, validator: Callable[[Any], bool]):
        """Set validator function for configuration key"""
        self._validators[key] = validator

    def get(self, key: str, default: Any = None) -> Any:
        """Get configuration value with validation"""
        # Check explicit value
        if key in self._data:
            value = self._data[key]
        # Check default
        elif key in self._defaults:
            value = self._defaults[key]
        # Use provided default
        else:
            value = default

        # Validate if validator exists
        if key in self._validators and value is not None:
            if not self._validators[key](value):
                raise ConfigurationError(f"Invalid value for '{key}': {value}")

        return value

    def require(self, key: str) -> Any:
        """Get required configuration value"""
        if key not in self._data and key not in self._defaults:
            raise ConfigurationError(f"Required configuration missing: {key}")
        return self.get(key)

    def validate_all(self):
        """Validate all configuration values"""
        for key, validator in self._validators.items():
            value = self.get(key)
            if value is not None and not validator(value):
                raise ConfigurationError(f"Validation failed for '{key}': {value}")

# Usage example
def create_inference_config() -> RobustConfig:
    """Create robust inference configuration"""
    config = RobustConfig(env_prefix="INFERENCE_")

    # Set defaults
    config.set_default("temperature", 0.7)
    config.set_default("max_tokens", 1000)
    config.set_default("timeout", 30.0)

    # Set validators
    config.set_validator("temperature", lambda x: 0.0 <= x <= 2.0)
    config.set_validator("max_tokens", lambda x: x > 0)
    config.set_validator("timeout", lambda x: x > 0)
    config.set_validator("api_key", lambda x: len(x) > 10)

    return config

# Usage
try:
    config = create_inference_config()
    config.validate_all()

    api_key = config.require("api_key")
    temperature = config.get("temperature")
    max_tokens = config.get("max_tokens")

except ConfigurationError as exc:
    logger.error(f"Configuration error: {exc}")
    raise
```

