from dataclasses import dataclass, field, InitVar
from typing import Any, ClassVar
import json

@dataclass
class APIEndpoint:
    """Advanced dataclass with computed fields and validation"""
    path: str
    method: str = "GET"
    auth_required: bool = True
    rate_limit: int = 100

    # Class variable (shared across instances)
    _registry: ClassVar[dict[str, "APIEndpoint"]] = {}

    # Init-only variable (not stored as field)
    validate_path: InitVar[bool] = True

    # Field with factory function
    metadata: dict[str, Any] = field(default_factory=dict)

    # Computed field (not in __init__)
    full_signature: str = field(init=False)

    def __post_init__(self, validate_path: bool):
        """Post-initialization processing"""
        if validate_path and not self.path.startswith('/'):
            raise ValueError(f"Path must start with '/': {self.path}")

        # Compute derived field
        self.full_signature = f"{self.method} {self.path}"

        # Register endpoint
        self._registry[self.full_signature] = self

    @classmethod
    def get_endpoint(cls, method: str, path: str) -> "APIEndpoint | None":
        """Class method to retrieve registered endpoints"""
        signature = f"{method} {path}"
        return cls._registry.get(signature)

    def to_openapi(self) -> dict:
        """Convert to OpenAPI specification"""
        return {
            "path": self.path,
            "method": self.method.lower(),
            "security": [{"bearerAuth": []}] if self.auth_required else [],
            "x-rate-limit": self.rate_limit,
            **self.metadata
        }

# Advanced field customization
@dataclass
class ProcessingConfig:
    """Configuration with validation and transformation"""

    # Field with custom validator
    batch_size: int = field(default=32)

    # Field with transformation
    model_path: str = field(default="")

    # Private field (convention)
    _internal_state: dict = field(default_factory=dict, repr=False)

    def __setattr__(self, name: str, value: Any):
        """Custom attribute setting with validation"""
        if name == "batch_size" and value <= 0:
            raise ValueError("batch_size must be positive")
        elif name == "model_path" and value:
            # Normalize path
            value = os.path.abspath(value)

        super().__setattr__(name, value)
