```python
from typing import Protocol, runtime_checkable, TypeVar, Generic, Union, Literal
from abc import abstractmethod
import inspect

T = TypeVar("T")
U = TypeVar("U")

@runtime_checkable
class Serializable(Protocol):
    """Protocol for objects that can be serialized"""
    def to_dict(self) -> dict: ...
    @classmethod
    def from_dict(cls, data: dict) -> "Serializable": ...

@runtime_checkable
class Repository(Protocol, Generic[T]):
    """Generic repository protocol"""
    async def create(self, item: T) -> T: ...
    async def get(self, id: str) -> T | None: ...
    async def list(self, filters: dict | None = None) -> list[T]: ...
    async def update(self, id: str, updates: dict) -> T | None: ...
    async def delete(self, id: str) -> bool: ...

class TypedRepository(Repository[T]):
    """Concrete implementation with type safety"""

    def __init__(self, item_type: type[T]):
        self.item_type = item_type
        self._storage: dict[str, T] = {}

    async def create(self, item: T) -> T:
        if not isinstance(item, self.item_type):
            raise TypeError(f"Expected {self.item_type}, got {type(item)}")

        item_id = getattr(item, 'id', str(len(self._storage)))
        self._storage[item_id] = item
        return item

    async def get(self, id: str) -> T | None:
        return self._storage.get(id)

# Usage with type safety
user_repo: Repository[User] = TypedRepository(User)
assert isinstance(user_repo, Repository)  # Runtime protocol check
