        words = f"Mock streaming response to: {messages[-1].content}".split()

        for i, word in enumerate(words):
            await asyncio.sleep(0.05)  # Simulate streaming delay

            chunk_message = Message(
                role="assistant",
                content=word + " " if i < len(words) - 1 else word,
                metadata={"chunk": i, "total_chunks": len(words)}
            )

            yield ChatResponse(
                choices=[Choice(message=chunk_message)],
                model=model_id
            )


class MockTelemetry:
    """Mock telemetry collector for examples."""

    def __init__(self):
        self.events: List[Dict[str, Any]] = []
        self.metrics: Dict[str, List[float]] = {}

    async def log_event(self, event: Dict[str, Any]):
        """Log an event."""
        event["timestamp"] = time.time()
        self.events.append(event)

    async def record_metric(self, name: str, value: float, tags: Dict[str, str] = None):
        """Record a metric value."""
        if name not in self.metrics:
            self.metrics[name] = []

        self.metrics[name].append(value)

        # Also log as event
        await self.log_event({
            "type": "metric",
            "name": name,
            "value": value,
            "tags": tags or {}
        })

    def get_events(self, event_type: str = None) -> List[Dict[str, Any]]:
        """Get logged events, optionally filtered by type."""
        if event_type:
            return [e for e in self.events if e.get("type") == event_type]
        return self.events.copy()

    def get_metric_values(self, name: str) -> List[float]:
        """Get all values for a metric."""
        return self.metrics.get(name, [])

    def reset(self):
        """Reset all collected data."""
        self.events.clear()
        self.metrics.clear()

