"""
Test helpers and utilities.
"""

import asyncio
import time
import logging
from contextlib import contextmanager
from typing import Generator, List, Dict, Any
from unittest.mock import patch


def async_test(coro):
    """Decorator to run async functions in tests."""
    def wrapper(*args, **kwargs):
        return asyncio.run(coro(*args, **kwargs))
    return wrapper


@contextmanager
def mock_time(start_time: float = 1000.0, increment: float = 1.0) -> Generator[None, None, None]:
    """Mock time.time() to return predictable values."""
    current_time = start_time

    def mock_time_func():
        nonlocal current_time
        result = current_time
        current_time += increment
        return result

    with patch('time.time', side_effect=mock_time_func):
        yield


@contextmanager
def capture_logs(logger_name: str = None, level: int = logging.INFO) -> Generator[List[logging.LogRecord], None, None]:
    """Capture log messages for testing."""
    records = []

    class TestHandler(logging.Handler):
        def emit(self, record):
            records.append(record)

    handler = TestHandler()
    handler.setLevel(level)

    if logger_name:
        logger = logging.getLogger(logger_name)
    else:
        logger = logging.getLogger()

    logger.addHandler(handler)
    original_level = logger.level
    logger.setLevel(level)

    try:
        yield records
    finally:
        logger.removeHandler(handler)
        logger.setLevel(original_level)


class PerformanceTimer:
    """Simple performance timer for examples."""

    def __init__(self):
        self.start_time = None
        self.end_time = None

    def __enter__(self):
        self.start_time = time.time()
        return self

    def __exit__(self, exc_type, exc_val, exc_tb):
        self.end_time = time.time()

    @property
    def elapsed(self) -> float:
        """Get elapsed time in seconds."""
        if self.start_time is None:
            return 0.0
        if self.end_time is None:
            return time.time() - self.start_time
        return self.end_time - self.start_time

    @property 
    def elapsed_ms(self) -> float:
        """Get elapsed time in milliseconds."""
        return self.elapsed * 1000


def assert_timing(func, min_time: float = None, max_time: float = None):
    """Assert that a function takes a certain amount of time."""
    start = time.time()
    result = func()
    elapsed = time.time() - start

    if min_time is not None:
        assert elapsed >= min_time, f"Function took {elapsed:.3f}s, expected at least {min_time:.3f}s"

    if max_time is not None:
        assert elapsed <= max_time, f"Function took {elapsed:.3f}s, expected at most {max_time:.3f}s"

    return result


async def assert_async_timing(coro, min_time: float = None, max_time: float = None):
    """Assert that an async function takes a certain amount of time."""
    start = time.time()
    result = await coro
    elapsed = time.time() - start

    if min_time is not None:
        assert elapsed >= min_time, f"Coroutine took {elapsed:.3f}s, expected at least {min_time:.3f}s"

    if max_time is not None:
        assert elapsed <= max_time, f"Coroutine took {elapsed:.3f}s, expected at most {max_time:.3f}s"

    return result

