    def _load_progress(self) -> Dict[str, Any]:
        """Load progress data from file."""
        import json

        if self.progress_file.exists():
            try:
                with open(self.progress_file, 'r') as f:
                    return json.load(f)
            except (json.JSONDecodeError, FileNotFoundError):
                pass

        # Initialize empty progress
        progress = {}
        for module_id in self.modules:
            progress[module_id] = {
                "examples_completed": [],
                "tests_completed": [],
                "last_accessed": None,
                "notes": []
            }
        return progress

    def _save_progress(self):
        """Save progress data to file."""
        import json
        from datetime import datetime

        # Update timestamp
        for module_data in self.progress_data.values():
            if module_data.get("examples_completed") or module_data.get("tests_completed"):
                module_data["last_accessed"] = datetime.now().isoformat()

        with open(self.progress_file, 'w') as f:
            json.dump(self.progress_data, f, indent=2)

    def check_file_exists(self, module_id: str, filename: str, file_type: str) -> bool:
        """Check if a module file exists."""
        if file_type == "example":
            filepath = self.base_path / module_id / "examples" / filename
        elif file_type == "test":
            filepath = self.base_path / module_id / "tests" / filename
        else:
            return False

        return filepath.exists()

    def mark_completed(self, module_id: str, filename: str, file_type: str):
        """Mark a file as completed."""
        if module_id not in self.progress_data:
            return False

        key = f"{file_type}s_completed"
        if filename not in self.progress_data[module_id][key]:
            self.progress_data[module_id][key].append(filename)
            self._save_progress()
            return True
        return False

    def get_completion_stats(self) -> Dict[str, Dict[str, Any]]:
        """Get completion statistics for all modules."""
        stats = {}

        for module_id, module_info in self.modules.items():
            examples_total = len(module_info["examples"])
            tests_total = len(module_info["tests"])

            examples_completed = len(self.progress_data[module_id]["examples_completed"])
            tests_completed = len(self.progress_data[module_id]["tests_completed"])

            # Check which files actually exist
            examples_existing = sum(1 for ex in module_info["examples"] 
                                   if self.check_file_exists(module_id, ex, "example"))
            tests_existing = sum(1 for test in module_info["tests"] 
                                if self.check_file_exists(module_id, test, "test"))

            stats[module_id] = {
                "title": module_info["title"],
                "description": module_info["description"],
                "examples": {
                    "completed": examples_completed,
                    "total": examples_existing,
                    "percentage": (examples_completed / examples_existing * 100) if examples_existing > 0 else 0
                },
                "tests": {
                    "completed": tests_completed,
                    "total": tests_existing,
                    "percentage": (tests_completed / tests_existing * 100) if tests_existing > 0 else 0
                },
                "overall_percentage": (
                    (examples_completed + tests_completed) / 
                    (examples_existing + tests_existing) * 100
                ) if (examples_existing + tests_existing) > 0 else 0
            }

        return stats

    def get_next_recommendation(self) -> Dict[str, str]:
        """Get next recommended learning step."""
        for module_id, module_info in self.modules.items():
            module_progress = self.progress_data[module_id]

            # Check for incomplete examples
            for example in module_info["examples"]:
                if (example not in module_progress["examples_completed"] and 
                    self.check_file_exists(module_id, example, "example")):
                    return {
                        "type": "example",
                        "module": module_id,
                        "file": example,
                        "title": module_info["title"],
                        "command": f"python {module_id}/examples/{example}"
                    }

            # Check for incomplete tests
            for test in module_info["tests"]:
                if (test not in module_progress["tests_completed"] and 
                    self.check_file_exists(module_id, test, "test")):
                    return {
                        "type": "test",
                        "module": module_id,
                        "file": test,
                        "title": module_info["title"],
                        "command": f"pytest {module_id}/tests/{test} -v"
                    }

        return {"type": "complete", "message": "ðŸŽ‰ All modules completed!"}

    def display_progress(self):
        """Display current progress."""
        console.print(Panel("ðŸŽ“ AI Architecture Patterns - Learning Progress", style="bold blue"))

        stats = self.get_completion_stats()

        # Overall progress table
        table = Table(title="ðŸ“Š Module Progress Overview")
        table.add_column("Module", style="cyan")
        table.add_column("Title", style="green")
        table.add_column("Examples", style="yellow")
        table.add_column("Tests", style="magenta")
        table.add_column("Overall", style="blue")

        total_items = 0
        completed_items = 0

        for module_id, stat in stats.items():
            examples_progress = f"{stat['examples']['completed']}/{stat['examples']['total']} ({stat['examples']['percentage']:.0f}%)"
            tests_progress = f"{stat['tests']['completed']}/{stat['tests']['total']} ({stat['tests']['percentage']:.0f}%)"
            overall_progress = f"{stat['overall_percentage']:.0f}%"

            table.add_row(
                module_id,
                stat["title"],
                examples_progress,
                tests_progress,
                overall_progress
            )

            total_items += stat['examples']['total'] + stat['tests']['total']
            completed_items += stat['examples']['completed'] + stat['tests']['completed']

        console.print(table)

        # Overall progress bar
        if total_items > 0:
            overall_percentage = completed_items / total_items * 100
            console.print(f"\nðŸŽ¯ Overall Progress: {completed_items}/{total_items} ({overall_percentage:.1f}%)")

            with Progress(
                TextColumn("[progress.description]{task.description}"),
                BarColumn(),
                TextColumn("[progress.percentage]{task.percentage:>3.0f}%"),
                TimeElapsedColumn(),
                console=console
            ) as progress:
                task = progress.add_task("Learning Progress", total=total_items)
                progress.update(task, completed=completed_items)
                import time
                time.sleep(0.1)  # Brief pause to show the bar

        # Next recommendation
        next_step = self.get_next_recommendation()
        if next_step["type"] != "complete":
            console.print(f"\n[bold green]ðŸš€ Next Recommended Step:[/bold green]")
            console.print(f"Module: {next_step['title']} ({next_step['module']})")
            console.print(f"File: {next_step['file']}")
            console.print(f"Run: [bold cyan]{next_step['command']}[/bold cyan]")
        else:
            console.print(f"\n[bold green]{next_step['message']}[/bold green]")

    def interactive_menu(self):
        """Show interactive menu for navigation."""
        while True:
            console.print("\n" + "="*60)
            console.print("[bold blue]ðŸŽ“ AI Architecture Patterns - Interactive Menu[/bold blue]")
            console.print("1. Show progress")
            console.print("2. Run next recommended step")
            console.print("3. Mark item as completed")
            console.print("4. List all examples")
            console.print("5. Run specific example")
            console.print("6. Run tests")
            console.print("0. Exit")

            choice = input("\nEnter your choice (0-6): ").strip()

            if choice == "0":
                console.print("Happy learning! ðŸš€")
                break
            elif choice == "1":
                self.display_progress()
            elif choice == "2":
                self._run_next_step()
            elif choice == "3":
                self._mark_completed_interactive()
            elif choice == "4":
                self._list_examples()
            elif choice == "5":
                self._run_specific_example()
            elif choice == "6":
                self._run_tests()
            else:
                console.print("[red]Invalid choice. Please try again.[/red]")

    def _run_next_step(self):
        """Run the next recommended step."""
        next_step = self.get_next_recommendation()
        if next_step["type"] == "complete":
            console.print(f"[bold green]{next_step['message']}[/bold green]")
            return

        console.print(f"[bold green]Running: {next_step['command']}[/bold green]")
        os.system(next_step["command"])

        # Ask if completed successfully
        completed = input("\nDid this complete successfully? (y/n): ").lower().startswith('y')
        if completed:
            self.mark_completed(next_step["module"], next_step["file"], next_step["type"])
            console.print("[green]âœ… Marked as completed![/green]")

    def _mark_completed_interactive(self):
        """Interactive interface to mark items as completed."""
        console.print("[bold yellow]Mark item as completed:[/bold yellow]")
        console.print("1. Example")
        console.print("2. Test")

        item_type = input("Choose type (1-2): ").strip()
        if item_type not in ["1", "2"]:
            console.print("[red]Invalid choice.[/red]")
            return

        file_type = "example" if item_type == "1" else "test"

        # Show modules
        for i, (module_id, module_info) in enumerate(self.modules.items(), 1):
            console.print(f"{i}. {module_info['title']} ({module_id})")

        try:
            module_choice = int(input("Choose module (1-3): ")) - 1
            module_id = list(self.modules.keys())[module_choice]

            # Show files
            files = self.modules[module_id][f"{file_type}s"]
            for i, filename in enumerate(files, 1):
                status = "âœ…" if filename in self.progress_data[module_id][f"{file_type}s_completed"] else "â­•"
                console.print(f"{i}. {status} {filename}")

            file_choice = int(input(f"Choose {file_type} (1-{len(files)}): ")) - 1
            filename = files[file_choice]

            if self.mark_completed(module_id, filename, file_type):
                console.print(f"[green]âœ… {filename} marked as completed![/green]")
            else:
                console.print(f"[yellow]âš ï¸ {filename} was already marked as completed.[/yellow]")

        except (ValueError, IndexError):
            console.print("[red]Invalid selection.[/red]")

    def _list_examples(self):
        """List all examples with their status."""
        for module_id, module_info in self.modules.items():
            console.print(f"\n[bold cyan]{module_info['title']} ({module_id}):[/bold cyan]")

            console.print("  Examples:")
            for example in module_info["examples"]:
                status = "âœ…" if example in self.progress_data[module_id]["examples_completed"] else "â­•"
                exists = "ðŸ“" if self.check_file_exists(module_id, example, "example") else "âŒ"
                console.print(f"    {status} {exists} {example}")

            console.print("  Tests:")
            for test in module_info["tests"]:
                status = "âœ…" if test in self.progress_data[module_id]["tests_completed"] else "â­•"
                exists = "ðŸ“" if self.check_file_exists(module_id, test, "test") else "âŒ"
                console.print(f"    {status} {exists} {test}")

    def _run_specific_example(self):
        """Run a specific example."""
        self._list_examples()

        try:
            module_id = input("\nEnter module ID (e.g., 01-error-handling): ").strip()
            if module_id not in self.modules:
                console.print("[red]Invalid module ID.[/red]")
                return

            filename = input("Enter example filename: ").strip()
            if filename not in self.modules[module_id]["examples"]:
                console.print("[red]Example not found.[/red]")
                return

            command = f"python {module_id}/examples/{filename}"
            console.print(f"[bold green]Running: {command}[/bold green]")
            os.system(command)

        except KeyboardInterrupt:
            console.print("\n[yellow]Interrupted by user.[/yellow]")

    def _run_tests(self):
        """Run tests with options."""
        console.print("[bold yellow]Test Options:[/bold yellow]")
        console.print("1. Run all tests")
        console.print("2. Run tests for specific module")
        console.print("3. Run specific test file")

        choice = input("Choose option (1-3): ").strip()

        if choice == "1":
            command = "pytest -v"
        elif choice == "2":
            for i, (module_id, module_info) in enumerate(self.modules.items(), 1):
                console.print(f"{i}. {module_info['title']} ({module_id})")

            try:
                module_choice = int(input("Choose module (1-3): ")) - 1
                module_id = list(self.modules.keys())[module_choice]
                command = f"pytest {module_id}/tests/ -v"
            except (ValueError, IndexError):
                console.print("[red]Invalid choice.[/red]")
                return
        elif choice == "3":
            module_id = input("Enter module ID: ").strip()
            test_file = input("Enter test filename: ").strip()
            command = f"pytest {module_id}/tests/{test_file} -v"
        else:
            console.print("[red]Invalid choice.[/red]")
            return

        console.print(f"[bold green]Running: {command}[/bold green]")
        result = os.system(command)

        if result == 0:
            console.print("[green]âœ… Tests passed![/green]")
        else:
            console.print("[red]âŒ Some tests failed.[/red]")


def main():
    """Main entry point."""
    tracker = LearningPathTracker()

    if len(sys.argv) > 1:
        command = sys.argv[1]
        if command == "progress":
            tracker.display_progress()
        elif command == "next":
            next_step = tracker.get_next_recommendation()
            if next_step["type"] != "complete":
                console.print(f"Next: {next_step['command']}")
            else:
                console.print(next_step["message"])
        elif command == "interactive":
            tracker.interactive_menu()
        else:
            console.print("Usage: python progress_tracker.py [progress|next|interactive]")
    else:
        tracker.interactive_menu()


if __name__ == "__main__":
    main()
